import React, { useState } from 'react'
import Person from './components/Person'

const Filter = ({filter, handleFilter}) => {
  return (
    <div>Filter name <input value = {filter} onChange={handleFilter}/></div>
  )
}

const Persons = ({PersonsToShow}) => {
  return (
    <ul>
    {PersonsToShow.map(person => 
        <Person key={person.id} person={person} />
    )}
  </ul>
  )
}

const PersonForm = ({addPerson, newName, handleNameChange, newNumber, handleNumberChange}) => {
  return (
    <form onSubmit={addPerson}>
    <div> Name: <input value = {newName} onChange={handleNameChange}/></div>
    <div> Number: <input value = {newNumber} onChange={handleNumberChange}/></div>
    <div><button type="submit">add</button></div>
  </form>
  )
}

const App = () => {
  const [ persons, setPersons ] = useState([
    { id: 'Arto Hellas', name: 'Arto Hellas', number: '040-123456' },
    { id: 'Ada Lovelace', name: 'Ada Lovelace', number: '39-44-5323523' },
    { id: 'Dan Abramov', name: 'Dan Abramov', number: '12-43-234345' },
    { id: 'Mary Poppendieck', name: 'Mary Poppendieck', number: '39-23-6423122' }
  ])
  const [ newName, setNewName ] = useState('')
  const [ newNumber, setNewNumber ] = useState('')
  const [ filter, setFilter] = useState('')

  const PersonsToShow = !filter ? persons : persons.filter(person => person.name.toLowerCase().includes(filter))


  const addPerson = (event) => {
    event.preventDefault()
    const personObject = {
      id: newName,
      name: newName,
      number: newNumber,
    }
    if (persons.map(person => person.name).includes(newName)){
      alert(`${newName} is already added to phonebook`)
    } else {
      setPersons(persons.concat(personObject))
      setNewName('')
      setNewNumber('')
    }
  }

  const handleFilter = (event) => {
    console.log(event.target.value)
    setFilter(event.target.value)
  }

  const handleNameChange = (event) => {
    console.log(event.target.value)
    setNewName(event.target.value)
  }

  const handleNumberChange = (event) => {
    console.log(event.target.value)
    setNewNumber(event.target.value)
  }



  return (
    <div>
      <h2>Phonebook</h2>
      <Filter filter={filter} handleFilter={handleFilter}/>
      <h3>Add a new phone record</h3>
      <PersonForm addPerson={addPerson} 
        newName = {newName} 
        handleNameChange = {handleNameChange} 
        newNumber={newNumber} 
        handleNumberChange = {handleNumberChange}/>
      <h3>Numbers</h3>
      <Persons PersonsToShow={PersonsToShow} />
    </div>
  )
}

export default App

