import React, { useState, useEffect } from 'react'
import Person from './components/Person'
import personService from './services/personService'

const Filter = ({filter, handleFilter}) => {
  return (
    <div>Filter name <input value = {filter} onChange={handleFilter}/></div>
  )
}

const Persons = ({PersonsToShow, deletePerson}) => {
  return (
    <ul>
    {PersonsToShow.map((person, i) => 
        <Person key={i} person={person} deletePerson = {deletePerson} />
    )}
  </ul>
  )
}

const PersonForm = ({addPerson, newName, handleNameChange, newNumber, handleNumberChange}) => {
  return (
    <form onSubmit={addPerson}>
    <div> Name: <input value = {newName} onChange={handleNameChange}/></div>
    <div> Number: <input value = {newNumber} onChange={handleNumberChange}/></div>
    <div><button type="submit">add</button></div>
  </form>
  )
} 

const App = () => {
  const [ persons, setPersons ] = useState([])
  const [ newName, setNewName ] = useState('')
  const [ newNumber, setNewNumber ] = useState('')
  const [ filter, setFilter] = useState('')

  useEffect(() => {
    personService
      .getAll()
      .then(initialPersons => {
      setPersons(initialPersons)
    })
  }, [])

  console.log('render', persons.length, 'notes')

  const PersonsToShow = !filter ? persons : persons.filter(person => person.name.toLowerCase().includes(filter))


  const addPerson = (event) => {
    event.preventDefault()
    const personInList = persons.filter(person =>
        person.name === newName
    )

    const personToAdd = person[0]
    const updatedPerson = { ...personToAdd, number: newNumber }

    if (person.length !== 0) {
      if (window.confirm(`${personToAdd.name} is already added to the phonebook, replace the old number with a new one ?`)) {
        personService
          .update(updatedPerson.id, updatedPerson).then(returnedPerson => {
            console.log(`${returnedPerson.name} successfully updated`)
            setAllPersons(allPersons.map(personItem => personItem.id !== personToAdd.id ? personItem : returnedPerson))
            setNewName('')
            setNewNumber('')
            setMessage(
              `${updatedPerson.name} was successfully updated`
            )
            setTimeout(() => {
              setMessage(null)
            }, 5000)
          })
          .catch((error) => {
            console.log(error)
            setAllPersons(allPersons.filter(person => person.id !== updatedPerson.id))
            setNewName('')
            setNewNumber('')
            setMessage(
              `[ERROR] ${updatedPerson.name} was already deleted from server`
            )
            setTimeout(() => {
              setMessage(null)
            }, 5000)
          })
      }

    } else {
      personService
      .create(personObject)
        .then(returnedPerson => {
        setPersons(persons.concat(returnedPerson))
        setNewName('')
        setNewNumber('')
      })
    }
  }

  const deletePerson = (id) => {
    const deletedPerson = persons.filter(person => person.id === id)
    const name = deletedPerson[0].name
    if (window.confirm(`Delete ${name} from the phonebook?`)) {
      personService.remove(id)
      console.log(`${name} successfully deleted`)
      setPersons(persons.filter(person => person.id !== id))
    }
  }

  const handleFilter = (event) => {
    console.log(event.target.value)
    setFilter(event.target.value)
  }

  const handleNameChange = (event) => {
    console.log(event.target.value)
    setNewName(event.target.value)
  }

  const handleNumberChange = (event) => {
    console.log(event.target.value)
    setNewNumber(event.target.value)
  }



  return (
    <div>
      <h2>Phonebook</h2>
      <Filter filter={filter} handleFilter={handleFilter}/>
      <h3>Add a new phone record</h3>
      <PersonForm addPerson={addPerson} 
        newName = {newName} 
        handleNameChange = {handleNameChange} 
        newNumber={newNumber} 
        handleNumberChange = {handleNumberChange}/>
      <h3>Numbers</h3>
      <Persons PersonsToShow={PersonsToShow} deletePerson = {deletePerson} />
    </div>
  )
}

export default App

